import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet-routing-machine";
import "../styles.css"; 

const RouteFinder = () => {
  useEffect(() => {
    const map = L.map("map").setView([20.5937, 78.9629], 5);

    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution: "© OpenStreetMap contributors",
    }).addTo(map);

    let control = L.Routing.control({
      waypoints: [],
      routeWhileDragging: true,
    }).addTo(map);

    let startMarker, destinationMarker;
    let setStartOnMap = false, setDestinationOnMap = false;

    function addMarker(latlng, type) {
      if (type === "start") {
        if (startMarker) startMarker.remove();
        startMarker = L.marker(latlng, { draggable: true }).addTo(map);
      } else if (type === "destination") {
        if (destinationMarker) destinationMarker.remove();
        destinationMarker = L.marker(latlng, { draggable: true }).addTo(map);
      }
    }

    document.getElementById("set-start").addEventListener("click", () => {
      setStartOnMap = true;
      setDestinationOnMap = false;
    });

    document.getElementById("set-destination").addEventListener("click", () => {
      setDestinationOnMap = true;
      setStartOnMap = false;
    });

    map.on("click", function (e) {
      const latlng = e.latlng;
      if (setStartOnMap) {
        addMarker(latlng, "start");
        document.getElementById("start").value = `${latlng.lat}, ${latlng.lng}`;
        setStartOnMap = false;
      } else if (setDestinationOnMap) {
        addMarker(latlng, "destination");
        document.getElementById("destination").value = `${latlng.lat}, ${latlng.lng}`;
        setDestinationOnMap = false;
      }
    });

    document.getElementById("route-form").addEventListener("submit", async (event) => {
      event.preventDefault();
      const start = document.getElementById("start").value.split(",").map(Number);
      const destination = document.getElementById("destination").value.split(",").map(Number);

      if (start.length === 2 && destination.length === 2) {
        control.setWaypoints([L.latLng(start), L.latLng(destination)]);
        calculateFare(start, destination);
        fetchTrafficUpdates(destination);
        saveCommuteHistory(start, destination);
      }
    });

    async function calculateFare(start, destination) {
      const distance = Math.sqrt(
        Math.pow(destination[0] - start[0], 2) +
        Math.pow(destination[1] - start[1], 2)
      ) * 111; // Approximation in km
      const fare = distance * 5; // Assume fare rate is $5 per km
      document.getElementById("fare").innerText = `$${fare.toFixed(2)}`;
    }

    async function fetchTrafficUpdates(location) {
      const trafficData = [
        "Moderate traffic near your destination",
        "Heavy traffic on route due to road construction",
        "Clear roads ahead",
      ];
      const trafficList = document.getElementById("traffic-data");
      trafficList.innerHTML = "";
      trafficData.forEach((update) => {
        const li = document.createElement("li");
        li.textContent = update;
        trafficList.appendChild(li);
      });
    }

    async function saveCommuteHistory(start, destination) {
      const token = localStorage.getItem("authtoken");
      if (!token) {
        alert("Unauthorized! Please login again.");
        return;
      }

      const data = {
        start: { lat: start[0], lng: start[1] },
        destination: { lat: destination[0], lng: destination[1] },
        date: new Date().toISOString(),
      };

      await fetch("http://127.0.0.1:3000/saveCommuteHistory", {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}` // Send JWT token
        },
        body: JSON.stringify(data),
      });
    }

    return () => map.remove(); // Cleanup map instance
  }, []);

  return (
    <div>
      <h1>Advanced Route Finder</h1>
      <form id="route-form">
        <div className="input-group">
          <label>Start (Lat, Lng):</label>
          <input type="text" id="start" placeholder="e.g., 20.5937, 78.9629" required />
          <button type="button" id="set-start">Set Start on Map</button>
        </div>
        <div className="input-group">
          <label>Destination (Lat, Lng):</label>
          <input type="text" id="destination" placeholder="e.g., 28.6139, 77.2090" required />
          <button type="button" id="set-destination">Set Destination on Map</button>
        </div>
        <button type="submit">Find Route</button>
      </form>
      <div id="map" style={{ height: "500px", width: "100%" }}></div>
    </div>
  );
};

export default RouteFinder;
